//Swapping a number using 3rd variable

package com.class6Assignment;

public class SwapDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int a=10;
int b=20;
int c;
System.out.println("value of a="+a+" and  b="+b);
 c=a;//value of a assign to c
 a=b;//value of b assign to a
 b=c;
 System.out.println("Swapped values of a="+a+" and b="+b);



	}

}
